import React from 'react';
import { motion, AnimatePresence } from 'framer-motion';
import { FiX, FiHeart, FiShare2, FiDownload, FiArrowLeft, FiArrowRight } from 'react-icons/fi';

const ImageModal = ({ image, isOpen, onClose, onNext, onPrev, hasNext, hasPrev }) => {
  if (!isOpen) return null;

  return (
    <AnimatePresence>
      {isOpen && (
        <motion.div
          initial={{ opacity: 0 }}
          animate={{ opacity: 1 }}
          exit={{ opacity: 0 }}
          className="fixed inset-0 bg-black/95 z-50 flex items-center justify-center p-4"
          onClick={onClose}
        >
          <motion.div
            initial={{ scale: 0.8, opacity: 0 }}
            animate={{ scale: 1, opacity: 1 }}
            exit={{ scale: 0.8, opacity: 0 }}
            className="relative max-w-6xl max-h-full w-full"
            onClick={(e) => e.stopPropagation()}
          >
            {/* Close Button */}
            <button
              onClick={onClose}
              className="absolute -top-4 -right-4 w-12 h-12 bg-white rounded-full flex items-center justify-center text-gray-800 hover:text-amber-600 transition-colors duration-300 z-10 shadow-lg hover:scale-110"
            >
              <FiX className="text-xl" />
            </button>

            {/* Navigation Arrows */}
            {hasPrev && (
              <button
                onClick={onPrev}
                className="absolute left-4 top-1/2 transform -translate-y-1/2 w-12 h-12 bg-white/90 backdrop-blur-sm rounded-full flex items-center justify-center text-gray-800 hover:text-amber-600 transition-all duration-300 z-10 shadow-lg hover:scale-110"
              >
                <FiArrowLeft className="text-xl" />
              </button>
            )}

            {hasNext && (
              <button
                onClick={onNext}
                className="absolute right-4 top-1/2 transform -translate-y-1/2 w-12 h-12 bg-white/90 backdrop-blur-sm rounded-full flex items-center justify-center text-gray-800 hover:text-amber-600 transition-all duration-300 z-10 shadow-lg hover:scale-110"
              >
                <FiArrowRight className="text-xl" />
              </button>
            )}

            {/* Image */}
            <div className="relative rounded-2xl overflow-hidden bg-white">
              <img
                src={image.url}
                alt={image.title}
                className="w-full h-auto max-h-[80vh] object-contain"
              />

              {/* Image Info */}
              <div className="absolute bottom-0 left-0 right-0 bg-gradient-to-t from-black/80 to-transparent p-6 text-white">
                <div className="flex items-center justify-between mb-3">
                  <span className="inline-block px-3 py-1 bg-amber-500 rounded-full text-xs font-semibold uppercase tracking-wider">
                    {image.category}
                  </span>
                  <div className="flex gap-2">
                    <button className="w-10 h-10 bg-white/20 backdrop-blur-sm rounded-full flex items-center justify-center hover:bg-white/30 transition-colors duration-300">
                      <FiHeart className="text-lg" />
                    </button>
                    <button className="w-10 h-10 bg-white/20 backdrop-blur-sm rounded-full flex items-center justify-center hover:bg-white/30 transition-colors duration-300">
                      <FiShare2 className="text-lg" />
                    </button>
                    <button className="w-10 h-10 bg-white/20 backdrop-blur-sm rounded-full flex items-center justify-center hover:bg-white/30 transition-colors duration-300">
                      <FiDownload className="text-lg" />
                    </button>
                  </div>
                </div>
                <h3 className="font-cinzel text-xl font-semibold mb-2">{image.title}</h3>
                <p className="text-amber-100">{image.description}</p>
              </div>
            </div>

            {/* Keyboard Shortcuts Hint */}
            <div className="text-center mt-4 text-white/60 text-sm">
              Use ← → arrow keys to navigate • Esc to close
            </div>
          </motion.div>
        </motion.div>
      )}
    </AnimatePresence>
  );
};

export default ImageModal;
